mapper.select:
SELECT coursedata_no, coursedata_title, coursedata_writer, coursedata_content, 
coursedata_registerdate,
coursedata_file, coursedata_status
FROM(
	SELECT list.*, rownum as rnum
	FROM (

		WHERE course_no = #{course_no}
		ORDER BY coursedata_no DESC
		) list
	)



			rnum between #{start_row} and #{end_row}

mapper.update:
UPDATE board_coursedata SET coursedata_title = #{coursedata_title},
coursedata_file = #{coursedata_file},
coursedata_content = #{coursedata_content}, coursedata_registerdate = sysdate WHERE coursedata_no = #{coursedata_no}
