mapper.insert:
select board_inquiry_seq.nextval from dual
Insert into board_inquiry ( inquiry_no, parent_no, inquiry_title, inquiry_content
) values( #{inquiry_no}, #{parent_no}, #{inquiry_title}, #{inquiry_content}
)

mapper.update:
		Update board_inquiry
		SET inquiry_title = #{inquiry_title},
		inquiry_content =
		#{inquiry_content}
		where inquiry_no = #{inquiry_no}
